import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub } from "@fortawesome/free-brands-svg-icons"; 


function Fichas(props){
    return(
        <div>
            <a className="transition ease-in-out delay-150  hover:-translate-y-1 hover:scale-110 hover:bg-indigo-500 duration-300 h-[85px] w-2/4 border flex flex-row items-center justify-center mt-7" href={props.link}>
                <FontAwesomeIcon className="text-white h-12 mr-5" icon={faGithub} />
                <p>{props.name}</p>
            </a>
        </div>
    )

}

export default Fichas;
// https://github.com/CristianSanchezSierra
