import React from "react";
import Nav from "../componentes/nav";
import img from '../imgs/click.jpg'

const Inicio =()=>{
    function reveal() {
        var reveals = document.querySelectorAll(".reveal");
        for (var i = 0; i < reveals.length; i++) {
          var windowHeight = window.innerHeight;
          var elementTop = reveals[i].getBoundingClientRect().top;
          var elementVisible = 150;
      
          if (elementTop < windowHeight - elementVisible) {
            reveals[i].classList.add("active");
          } else {
            reveals[i].classList.remove("active");
          }
        }
      }
      window.addEventListener("scroll", reveal);

    return(
        <div className="bg-gradient-to-r from-black to-gray-800 h-screen w-full items-center flex flex-col">
            <Nav/>
            <div className="flex flex-col w-full min-h-[100%]">
                <div className="w-full h-full flex flex-row items-center justify-center text-white">
                    <h1 className="text-4xl mt-[80px] w-[40%]">Welcome to my web. <br /> You can know about my profesional profile and also personal things that i have the pleasure to share.</h1>
                    <img className="h-[70%] w-[30%] mt-[150px] ml-[100px] rounded-full mr-[50px]" src={img} />
                </div>
            </div>

            <div className="reveal min-h-[70%] bg-black w-2/4 text-white"></div>
                
        </div>
    )

}

export default Inicio;
